import { useRef, useState } from 'react';
import './App.css';
import Canvas from './canvas/Canvas';
import Output from './output/Output';
import Buttons from './buttons/Buttons';
import Parameters from './buttons/Parameters';


function App() {
	const canvasRef = useRef<HTMLCanvasElement>(null);
	const [eyeL, setEyeL] = useState<number>(4);
	const [eyeR, setEyeR] = useState<number>(4);
	const [mouth, setMouth] = useState<number>(4);
	const [nose, setNose] = useState<number>(4);
	const [other, setOther] = useState<number>(4);
	const drawingstate = useRef<Boolean>(true);
	const [brushsize, setBrushsize] = useState<number>(4);
	const [erasersize, setErasersize] = useState<number>(4);
	return (
		<body className="h-screen bg-gradient-to-r from-yellow-400 via-red-500 to-pink-500">
			<div className="upside">
				<Buttons canvasRef={canvasRef} eyeL={eyeL} eyeR={eyeR} nose={nose} mouth={mouth}
				other={other} drawingstate={drawingstate} brushsize={brushsize} erasersize={erasersize} setBrushsize={setBrushsize} setErasersize={setErasersize}/>
				<Parameters canvasRef={canvasRef} eyeL={eyeL} eyeR={eyeR} nose={nose} mouth={mouth} other={other}
				setEyeL={setEyeL} setEyeR={setEyeR} setMouth={setMouth} setNose={setNose} setOther={setOther}/>
			</div>
			<div className="downside">
				<Canvas canvasRef={canvasRef} eyeL={eyeL} eyeR={eyeR} nose={nose} mouth={mouth}
				other={other} drawingstate={drawingstate} brushsize={brushsize} erasersize={erasersize} setBrushsize={setBrushsize} setErasersize={setErasersize}/>
				<Output/>
			</div>
		</body>
	);
}

export default App;
