import { useRef, useState } from 'react';
import './App.css';
import Canvas from './canvas/Canvas';
import Output from './output/Output';
import Buttons from './buttons/Buttons';


function App() {
	const canvasRef = useRef<HTMLCanvasElement>(null);
	const eyeLRef = useRef<number>(0);
	const eyeRRef = useRef<number>(0);
	const mouthRef = useRef<number>(0);
	const noseRef = useRef<number>(0);
	const otherRef = useRef<number>(0);
	const brushsize = useState<number>(5);
	const erasersize = useState<number>(5);
	const drawingstate = useRef<Boolean>(true);
	return (
		<body className="h-screen bg-gradient-to-r from-yellow-400 via-red-500 to-pink-500">
			<Buttons canvasRef={canvasRef} eyeLRef={eyeLRef} eyeRRef={eyeRRef} noseRef={noseRef} mouthRef={mouthRef} otherRef={otherRef} drawingstate={drawingstate} brushsize={brushsize} erasersize={erasersize}/>
			<Canvas canvasRef={canvasRef} eyeLRef={eyeLRef} eyeRRef={eyeRRef} noseRef={noseRef} mouthRef={mouthRef} otherRef={otherRef} drawingstate={drawingstate} brushsize={brushsize} erasersize={erasersize}/>
			<Output/>
		</body>
	);
}

export default App;
