import { useRef, useState, useEffect } from 'react';
import './App.css';
import Canvas from './canvas/Canvas';
import Output from './output/Output';
import Buttons from './buttons/Buttons';
import Parameters from './buttons/Parameters';
import axios from 'axios';

interface Record {
	x: number;
	y: number;
	c: boolean;
	s: number;
};

function App() {
	const canvasRef = useRef<HTMLCanvasElement>(null);
	const [eyeL, setEyeL] = useState<number>(0.01);
	const [eyeR, setEyeR] = useState<number>(0.01);
	const [mouth, setMouth] = useState<number>(0.01);
	const [nose, setNose] = useState<number>(0.01);
	const [other, setOther] = useState<number>(0.01);
	const [brushsize, setBrushsize] = useState<number>(4);
	const [erasersize, setErasersize] = useState<number>(10);
	const [drawingstate, setDrawingstate] = useState<boolean>(true);

	const [undoSteps, setUndoSteps] = useState<Array<Record | never[]>>([]);
	const [redoSteps, setRedoSteps] = useState<Array<Record | never[]>>([]);
	const [undo, setUndo] = useState<number>(0);
	const [redo, setRedo] = useState<number>(0);

	const [realtime, setRealtime] = useState<boolean>(true);
	const [loading, setLoading] = useState<boolean>(false);
	const [background, setBackground] = useState<boolean>(true);

	const fetchImage = () => {
		if (!canvasRef.current) { return; }
		if (loading) { return; }
		try {
			// loading 상태를 true 로 바꿉니다.
			setLoading(true);
			let data = new FormData();
			canvasRef.current.toBlob(function (blob) {
				if (!blob) { return };
				data.append('data', blob);
				// axios.post('http://27.96.135.229:8900/', { params : { file :data},
				// 		headers: { 'Content-Type': 'multipart/form-data',
				// 		},
				// 	});
				const res = axios.get('http://27.96.135.229:8900/', {
						headers: { 'Content-Type': 'multipart/form-data', },
				});
				console.log(res);
			});
		} catch (e) {
			console.log(e);
			return ;
		};
		setLoading(false);
		return ;
	};

	useEffect(() => {
		fetchImage();
	}, []);


	return (
		<body className="h-screen bg-gradient-to-r from-yellow-400 via-red-500 to-pink-500">
			<div className="App">
				<div className="upside">
					<div className="cvcoin"></div>
					<Buttons canvasRef={canvasRef} eyeL={eyeL} eyeR={eyeR} nose={nose} mouth={mouth} other={other}
					drawingstate={drawingstate} brushsize={brushsize} erasersize={erasersize} setBrushsize={setBrushsize} setErasersize={setErasersize} setDrawingstate={setDrawingstate}
					undoSteps={undoSteps} setUndoSteps={setUndoSteps} redoSteps={redoSteps} setRedoSteps={setRedoSteps}
					undo={undo} setUndo={setUndo} redo={redo} setRedo={setRedo} realtime={realtime} setRealtime={setRealtime}
					background={background} loading={loading} setBackground={setBackground} setLoading={setLoading} fetchImage={fetchImage}/>
					<Parameters canvasRef={canvasRef} eyeL={eyeL} eyeR={eyeR} nose={nose} mouth={mouth} other={other} realtime={realtime}
					setEyeL={setEyeL} setEyeR={setEyeR} setMouth={setMouth} setNose={setNose} setOther={setOther}/>
				</div>
				<div className="downside">
					<Canvas canvasRef={canvasRef} eyeL={eyeL} eyeR={eyeR} nose={nose} mouth={mouth} other={other}
					drawingstate={drawingstate} brushsize={brushsize} erasersize={erasersize}
					undoSteps={undoSteps} setUndoSteps={setUndoSteps} redoSteps={redoSteps} setRedoSteps={setRedoSteps}
					undo={undo} setUndo={setUndo} redo={redo} setRedo={setRedo} realtime={realtime}
					background={background} loading={loading} setLoading={setLoading} fetchImage={fetchImage}/>
					<Output loading={loading}/>
				</div>
			</div>
		</body>
	);
}

export default App;
