import '../styles/styles.css';

interface Record {
	ox: number;
	oy: number;
	nx: number;
	ny: number;
	c: boolean;
	s: number;
};

interface InputProps {
	canvasRef: React.RefObject<HTMLCanvasElement>;
	eyeL: number;
	eyeR: number;
	nose: number;
	mouth: number;
	other: number;
	drawingstate: boolean;
	brushsize: number;
	erasersize: number;
	setBrushsize: React.Dispatch<React.SetStateAction<number>>;
	setErasersize: React.Dispatch<React.SetStateAction<number>>;
	setDrawingstate: React.Dispatch<React.SetStateAction<boolean>>;
	undoSteps: Array<Array<Record>>;
	undo: number;
	redo: number;
	setUndo: React.Dispatch<React.SetStateAction<number>>;
	setRedo: React.Dispatch<React.SetStateAction<number>>;
	background: boolean;
	loading: boolean;
	setBackground: React.Dispatch<React.SetStateAction<boolean>>;
	setLoading: React.Dispatch<React.SetStateAction<boolean>>;
	realtime: boolean;
	setRealtime: React.Dispatch<React.SetStateAction<boolean>>;
	fetchImage: () => void;
}


function Buttons({canvasRef, eyeL, eyeR, nose, mouth, other, drawingstate, undoSteps, undo, redo,
	brushsize, erasersize, setBrushsize, setErasersize, setDrawingstate, setUndo, setRedo,
	background, loading, setBackground, setLoading, realtime, setRealtime, fetchImage} : InputProps) {

	const convertCanvases = () => {
		if (!canvasRef.current) { return; }
		fetchImage();
	}

	const handleBackground = (event: React.ChangeEvent<HTMLInputElement>) : number | undefined => {
		if (!canvasRef.current) { return; }
		setBackground(!background);
	}

	const handleRealtime = () => {
		if (!canvasRef.current) { return; }
		setRealtime(!realtime);
	}

	const handleBrush = (event: React.ChangeEvent<HTMLInputElement>) : number | undefined => {
		if (!canvasRef.current) { return; }
		const size:string = event.target.value;
		setBrushsize(+size);
	}
	const handleEraser = (event: React.ChangeEvent<HTMLInputElement>) : number | undefined => {
		if (!canvasRef.current) { return; }
		const size:string = event.target.value;
		setErasersize(+size);
	}
	const modeChange = () => {
		if (!canvasRef.current) { return; }
		setDrawingstate(!drawingstate);
	}
	const clearCanvas = () => {
		if (!canvasRef.current) { return; }
		const canvas: HTMLCanvasElement = canvasRef.current;

		const context = canvas.getContext('2d');
		if (context) {
			context.fillStyle = "white";  // ÏÑ† ÏÉâÍπî
			context.fillRect(0, 0, 512, 512);
		}
	}

	// canvasÏóê ÏÑ†ÏùÑ Í∏ãÎäî Ìï®Ïàò
	const drawLines = (record: Record) => {
		if (!canvasRef.current) { return; }
		const canvas: HTMLCanvasElement = canvasRef.current;
		const context = canvas.getContext('2d');

		if (context) {
			if (record.c) {
				context.strokeStyle = "black";  // ÏÑ† ÏÉâÍπî
				context.lineWidth = brushsize;		// ÏÑ† ÍµµÍ∏∞
			} else {
				context.strokeStyle = "white";  // ÏÑ† ÏÉâÍπî
				context.lineWidth = erasersize;		// ÏÑ† ÍµµÍ∏∞
			}

			context.beginPath();
			context.moveTo(record.ox, record.oy);
			context.lineTo(record.nx, record.ny);
			context.closePath();
			context.stroke();
		}
	};

	const undoLastOperation = () => {
		if (!canvasRef.current) { return; }
		const context: CanvasRenderingContext2D |null = canvasRef.current.getContext('2d');
		if (!context) { return; }
		if (undo > 0) {
			context.fillStyle = "white";  // ÏÑ† ÏÉâÍπî
			context.fillRect(0, 0, 512, 512);
			const data = undoSteps[undo - 1];
			data.forEach((record) => {
				drawLines(record);
			});
		};
		setUndo(undo - 1);
		console.log(undo);
	};

	const redoLastOperation = () => {
		return ;
		// if (!canvasRef.current) { return; }
		// const context: CanvasRenderingContext2D |null = canvasRef.current.getContext('2d');
		// if (!context) { return; }
		// if (redo > 0) {
		//   const data = redoSteps[redo];
		//   context.strokeStyle = "black";
		//   context.beginPath();
		//   context.lineWidth = 5;
		//   context.moveTo(data.x, data.y);
		//   data.forEach((item, index) => {
		// 	if (index !== 0) {
		// 	  context.lineTo(item.x, item.y);
		// 	  context.stroke();
		// 	}
		//   });
		//   context.closePath();
		//   const temp = {
		// 	...redoStep,
		// 	[redo]: []
		//   };
		//   setUndo(undo + 1);
		//   setRedo(redo - 1);
		//   setRedoStep(temp);
		//   setUndoSteps({
		// 	...undoSteps,
		// 	[undo + 1]: [...data]
		//   });
		// }
	  };

	return (
		<div className='Buttons1'>
			<div className='Buttonblocks'>
				<span> Background </span>
				<input type="checkbox" checked={background} onChange={handleBackground}></input>
				<span> Brush size : </span>
				<input type="range" id="paintRange" min="0.1" max="10" value={brushsize} step="0.1" onChange={handleBrush}/>
				<span> Eraser size : </span>
				<input type="range" id="eraseRange" min="0.1" max="100" value={erasersize} step="0.1" onChange={handleEraser}/>
			</div>
			<div className='Buttonblocks'>
				<button className={(realtime ? "bg-blue-500 hover:bg-blue-400 border-blue-700 hover:border-blue-500 text-white font-bold py-2 px-4 border-b-4  rounded" : "bg-red-500 hover:bg-red-400 border-red-700 hover:border-red-500 text-white font-bold py-2 px-4 border-b-4  rounded")} onClick={handleRealtime}>
				REALTIME { realtime ? <div> ON ‚úÖ</div>  : <div> OFFüö´</div>}
				</button>
				<button className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded" onClick={convertCanvases}>
				 <div>CONVERT üí£</div>
				</button>
				<button className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded" onClick={modeChange}>
					{drawingstate ?  <div>BRUSH ü™Ñ</div> : <div>ERASER üñ±</div>}
				</button>
			</div>
			<div className='Buttonblocks'>
				<button className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded" onClick={clearCanvas}>
				<div>CLEAR ü•°</div>
				</button>
				<button className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded" onClick={undoLastOperation}>
				 <div>UNDO ‚Ü©Ô∏è</div>
				</button>
				<button className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded" onClick={redoLastOperation}>
				<div>REDO ‚Ü™Ô∏è</div>
				</button>
			</div>
		</div>
	);
}

export default Buttons;
