import {useRef} from 'react';
import './App.css';
import Canvas from './canvas/Canvas';
import Output from './output/Output';


function App() {
	const canvasRef = useRef<HTMLCanvasElement>(null);
	canvasRef.current.getContext('2d').context.strokeStyle = "black";  // 선 색깔
	canvasRef.current.getContext('2d').context.lineJoin = 'round';	// 선 끄트머리(?)
	return (
		<body className="h-screen bg-gradient-to-r from-yellow-400 via-red-500 to-pink-500">
			<Canvas canvasRef={canvasRef} />
			<Output canvasRef={canvasRef} />
		</body>
	);
}

export default App;
