import React, { useRef, useState, useCallback, useEffect } from 'react';
import '../styles/styles.css';

interface InputProps {
	width: number;
	height: number;
	canvasRef: React.RefObject<HTMLCanvasElement>;
	eyeLRef: React.MutableRefObject<number>;
	eyeRRef: React.MutableRefObject<number>;
	noseRef: React.MutableRefObject<number>;
	mouthRef: React.MutableRefObject<number>;
	otherRef: React.MutableRefObject<number>;
}

interface Coordinate {
	x: number;
	y: number;
};

function Canvas({width, height, canvasRef, eyeLRef, eyeRRef, noseRef, mouthRef, otherRef} : InputProps) {

	const brushsize = useRef<number>(5);
	const erasersize = useRef<number>(5);
	const drawingstate = useRef<Boolean>(true);
	// Mouse pointer poisition
	const [mousePosition, setMousePosition] = useState<Coordinate | undefined>(undefined);
	const [isPainting, setIsPainting] = useState(false);


	// 좌표 얻는 함수
	const getCoordinates = (event: MouseEvent): Coordinate | undefined => {
		if (!canvasRef.current) {
			return;
		}
		const canvas: HTMLCanvasElement = canvasRef.current;
		return {
			x: event.pageX - canvas.offsetLeft,
			y: event.pageY - canvas.offsetTop
		};
	};

	// Brush bar handling
	const handleBrush = (event: React.ChangeEvent<HTMLInputElement>) : number | undefined => {
		if (!canvasRef.current) {
			return;
		}
		const size:string = event.target.value;
		brushsize.current = +size;
	}

	// Eraser bar handling
	const handleEraser = (event: React.ChangeEvent<HTMLInputElement>) : number | undefined => {
		if (!canvasRef.current) {
			return;
		}
		const size:string = event.target.value;
		erasersize.current = +size;
	}

	// canvas에 선을 긋는 함수
	const drawLine = (originalMousePosition: Coordinate, newMousePosition: Coordinate) => {
	  if (!canvasRef.current) {
		return;
	  }
	  const canvas: HTMLCanvasElement = canvasRef.current;
	  const context = canvas.getContext('2d');

	  if (context) {
		if (drawingstate.current) {
			context.strokeStyle = "black";  // 선 색깔
			context.lineJoin = 'round';	// 선 끄트머리(?)
			context.lineWidth = brushsize.current;		// 선 굵기
		} else {
			context.strokeStyle = "white";  // 선 색깔
			context.lineJoin = 'round';	// 선 끄트머리(?)
			context.lineWidth = erasersize.current;		// 선 굵기
		}

		context.beginPath();
		context.moveTo(originalMousePosition.x, originalMousePosition.y);
		context.lineTo(newMousePosition.x, newMousePosition.y);
		context.closePath();

		context.stroke();
	  }
	};

	const startPaint = useCallback((event: MouseEvent) => {
	  const coordinates = getCoordinates(event);
	  if (coordinates) {
		setIsPainting(true);
		setMousePosition(coordinates);
	  }
	}, []);

	const paint = useCallback(
	  (event: MouseEvent) => {
		event.preventDefault();   // drag 방지
		event.stopPropagation();  // drag 방지

		if (isPainting) {
		  const newMousePosition = getCoordinates(event);
		  if (mousePosition && newMousePosition) {
			drawLine(mousePosition, newMousePosition);
			setMousePosition(newMousePosition);
		  }
		}
	  },
	  [isPainting, mousePosition]
	);

	const exitPaint = useCallback(() => {
	  setIsPainting(false);
	}, []);

	useEffect(() => {
		if (!canvasRef.current) {
		  return;
		}
		const canvas: HTMLCanvasElement = canvasRef.current;

		canvas.addEventListener('mousedown', startPaint);
		canvas.addEventListener('mousemove', paint);
		canvas.addEventListener('mouseup', exitPaint);
		canvas.addEventListener('mouseleave', exitPaint);

		return () => {
		  // Unmount 시 이벤트 리스터 제거
		  canvas.removeEventListener('mousedown', startPaint);
		  canvas.removeEventListener('mousemove', paint);
		  canvas.removeEventListener('mouseup', exitPaint);
		  canvas.removeEventListener('mouseleave', exitPaint);
		};
	  }, [startPaint, paint, exitPaint]);


	// Realtime Button

	// Convert Button

	const convertCanvas = () => {
		if (!canvasRef.current) {
			return;
		}
		const dataURL:string = canvasRef.current.toDataURL("image/png");
	}
	// Clear Button
	const clearCanvas = () => {
		if (!canvasRef.current) {
			return;
		}
		const canvas: HTMLCanvasElement = canvasRef.current;
		canvas.getContext('2d')!!.clearRect(0, 0, canvas.width, canvas.height);
	}

	// Undo Button

	// const undoLastOperation = () => {
	// 	if (undo > 0) {
	// 	  const data = undoSteps[undo];
	// 	  contextRef.current.strokeStyle = "white";
	// 	  contextRef.current.beginPath();
	// 	  contextRef.current.lineWidth = 5;
	// 	  contextRef.current.moveTo(data[0].offsetX, data[0].offsetY);
	// 	  data.forEach((item, index) => {
	// 		if (index !== 0) {
	// 		  contextRef.current.lineTo(item.offsetX, item.offsetY);
	// 		  contextRef.current.stroke();
	// 		}
	// 	  });
	// 	  contextRef.current.closePath();
	// 	  contextRef.current.strokeStyle = "black";
	// 	  const temp = {
	// 		...undoSteps,
	// 		[undo]: []
	// 	  };
	// 	  const te = {
	// 		...redoStep,
	// 		[redo + 1]: [...data]
	// 	  };
	// 	  setUndo(undo - 1);
	// 	  setRedo(redo + 1);
	// 	  setRedoStep(te);
	// 	  setUndoSteps(temp);
	// 	}
	//   };

	return (
		<div className="Block">
			<div className='Buttons'>
				<input type="range" id="paintRange" min="0.1" max="5" value="2.5" step="0.1" />
				<input type="range" id="eraseRange" min="0.1" max="5" value="2.5" step="0.1" />
				<button className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded" onClick={clearCanvas}>
				Real Time
				</button>
				<button className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded" onClick={clearCanvas}>
				Convert
				</button>
				<button className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded" onClick={clearCanvas}>
				Clear
				</button>
				<button className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded" onClick={clearCanvas}>
				Undo
				</button>
			</div>
			<canvas ref={canvasRef} height={height} width={width} className="canvas"/>
		</div>
	);
}

Canvas.defaultProps = {
	width: 512,
	height: 512
};

export default Canvas;
