import { useRef, useState } from 'react';
import './App.css';
import Canvas from './canvas/Canvas';
import Output from './output/Output';
import Buttons from './buttons/Buttons';
import Parameters from './buttons/Parameters';


function App() {
	const canvasRef = useRef<HTMLCanvasElement>(null);
	const [eyeL, setEyeL] = useState<number>(0.01);
	const [eyeR, setEyeR] = useState<number>(0.01);
	const [mouth, setMouth] = useState<number>(0.01);
	const [nose, setNose] = useState<number>(0.01);
	const [other, setOther] = useState<number>(0.01);
	const [brushsize, setBrushsize] = useState<number>(4);
	const [erasersize, setErasersize] = useState<number>(4);
	const [drawingstate, setDrawingstate] = useState<Boolean>(true);

	const [undoSteps, setUndoSteps] = useState<{}>({});
	const [redoSteps, setRedoSteps] = useState<{}>({});
	const [undo, setUndo] = useState<number>(0);
	const [redo, setRedo] = useState<number>(0);

	return (
		<body className="h-screen bg-gradient-to-r from-yellow-400 via-red-500 to-pink-500">
			<div className="App">
				<div className="upside">
					<Buttons canvasRef={canvasRef} eyeL={eyeL} eyeR={eyeR} nose={nose} mouth={mouth} other={other}
					drawingstate={drawingstate} brushsize={brushsize} erasersize={erasersize} setBrushsize={setBrushsize} setErasersize={setErasersize} setDrawingstate={setDrawingstate}
					undoSteps={undoSteps} setUndoSteps={setUndoSteps} redoSteps={redoSteps} setRedoSteps={setRedoSteps}
					undo={undo} setUndo={setUndo} redo={redo} setRedo={setRedo}/>
					<Parameters canvasRef={canvasRef} eyeL={eyeL} eyeR={eyeR} nose={nose} mouth={mouth} other={other}
					setEyeL={setEyeL} setEyeR={setEyeR} setMouth={setMouth} setNose={setNose} setOther={setOther}/>
				</div>
				<div className="downside">
					<Canvas canvasRef={canvasRef} eyeL={eyeL} eyeR={eyeR} nose={nose} mouth={mouth} other={other}
					drawingstate={drawingstate} brushsize={brushsize} erasersize={erasersize}/>
					<Output/>
				</div>
			</div>
		</body>
	);
}

export default App;
