import { useRef, useState } from 'react';
import './App.css';
import Canvas from './canvas/Canvas';
import Output from './output/Output';
import Buttons from './buttons/Buttons';
import Parameters from './buttons/Parameters';


function App() {
	const canvasRef = useRef<HTMLCanvasElement>(null);
	const eyeLRef = useRef<number>(0);
	const eyeRRef = useRef<number>(0);
	const mouthRef = useRef<number>(0);
	const noseRef = useRef<number>(0);
	const otherRef = useRef<number>(0);
	const drawingstate = useRef<Boolean>(true);
	const [brushsize, setBrushsize] = useState<number>(4);
	const [erasersize, setErasersize] = useState<number>(4);
	return (
		<body className="h-screen bg-gradient-to-r from-yellow-400 via-red-500 to-pink-500">
			<div className="upside">
				<Buttons className=""canvasRef={canvasRef} eyeLRef={eyeLRef} eyeRRef={eyeRRef} noseRef={noseRef} mouthRef={mouthRef}
				otherRef={otherRef} drawingstate={drawingstate} brushsize={brushsize} erasersize={erasersize} setBrushsize={setBrushsize} setErasersize={setErasersize}/>
				<Parameters canvasRef={canvasRef} eyeLRef={eyeLRef} eyeRRef={eyeRRef} noseRef={noseRef} mouthRef={mouthRef}
				otherRef={otherRef} drawingstate={drawingstate} brushsize={brushsize} erasersize={erasersize} setBrushsize={setBrushsize} setErasersize={setErasersize}/>
			</div>
			<div className="upside">
				<Canvas canvasRef={canvasRef} eyeLRef={eyeLRef} eyeRRef={eyeRRef} noseRef={noseRef} mouthRef={mouthRef}
				otherRef={otherRef} drawingstate={drawingstate} brushsize={brushsize} erasersize={erasersize} setBrushsize={setBrushsize} setErasersize={setErasersize}/>
				<Output/>
			</div>
		</body>
	);
}

export default App;
