import { useRef, useState } from 'react';
import './App.css';
import Canvas from './canvas/Canvas';
import Output from './output/Output';
import Buttons from './buttons/Buttons';
import Parameters from './buttons/Parameters';
import axios from 'axios';

function App() {
	const canvasRef = useRef<HTMLCanvasElement>(null);
	const outputRef = useRef<HTMLCanvasElement>(null);
	const [eyeL, setEyeL] = useState<number>(0.01);
	const [eyeR, setEyeR] = useState<number>(0.01);
	const [mouth, setMouth] = useState<number>(0.01);
	const [nose, setNose] = useState<number>(0.01);
	const [other, setOther] = useState<number>(0.01);
	const [brushsize, setBrushsize] = useState<number>(4);
	const [erasersize, setErasersize] = useState<number>(10);
	const [drawingstate, setDrawingstate] = useState<boolean>(true);

	const [realtime, setRealtime] = useState<boolean>(true);
	const [loading, setLoading] = useState<boolean>(false);
	const [background, setBackground] = useState<boolean>(true);

	const fetchImage = async () => {
		if (!canvasRef.current) { return; }
		if (!loading) {
			try {
				setLoading(true);
				const data = await canvasRef.current.toDataURL('image/png', 1.0);
				const bodies = await JSON.stringify({"image" : data, "eyeL" : eyeL, "eyeR" : eyeR, "mouth" : mouth, "nose" : nose, "other" : other})
				const result = await axios.post('http://27.96.135.189:8894', bodies, {
					headers: {
						'Content-Type': 'multipart/form-data',
					}})
				if (!outputRef.current) { return; }
				const canvas: HTMLCanvasElement = outputRef.current;
				const context = await canvas.getContext('2d');
				if (context) {
					var image = await new Image();
					image.crossOrigin = "Anonymous";
					console.log('Hello');
					image.src = 'http://27.96.135.189:8894/output.png';
					image.onload = async () => {
						if (!context) { return; }
						await context.drawImage(image, 0, 0);
					};
				}
			} catch (e) {
				setLoading(false);
				console.log(e);
				return ;
			};
			setLoading(false);
			return ;
		}
	};

	return (
		<body className="h-screen bg-gradient-to-r from-yellow-400 via-red-500 to-pink-500">
			<div className="App">
				<div className="upside">
					<div className="cvcoin"></div>
					<Buttons canvasRef={canvasRef}
					drawingstate={drawingstate} brushsize={brushsize} erasersize={erasersize} setBrushsize={setBrushsize} setErasersize={setErasersize} setDrawingstate={setDrawingstate}
					realtime={realtime} setRealtime={setRealtime}
					background={background} loading={loading} setBackground={setBackground} setLoading={setLoading} fetchImage={fetchImage}/>
					<Parameters canvasRef={canvasRef} eyeL={eyeL} eyeR={eyeR} nose={nose} mouth={mouth} other={other} realtime={realtime}
					setEyeL={setEyeL} setEyeR={setEyeR} setMouth={setMouth} setNose={setNose} setOther={setOther} outputRef={outputRef} fetchImage={fetchImage}/>
				</div>
				<div className="downside">
					<Canvas canvasRef={canvasRef}
					drawingstate={drawingstate} brushsize={brushsize} erasersize={erasersize} realtime={realtime}
					background={background} fetchImage={fetchImage}/>
					<Output loading={loading} outputRef={outputRef}/>
				</div>
			</div>
		</body>
	);
}

export default App;
