import '../styles/styles.css';

interface InputProps {
	canvasRef: React.RefObject<HTMLCanvasElement>;
	eyeL: number;
	eyeR: number;
	nose: number;
	mouth: number;
	other: number;
	realtime: boolean;
	setEyeL: React.Dispatch<React.SetStateAction<number>>;
	setEyeR: React.Dispatch<React.SetStateAction<number>>;
	setNose: React.Dispatch<React.SetStateAction<number>>;
	setMouth: React.Dispatch<React.SetStateAction<number>>;
	setOther: React.Dispatch<React.SetStateAction<number>>;
}

function Parameters({canvasRef, eyeL, eyeR, nose, mouth, other, realtime, setEyeL, setEyeR, setNose, setMouth, setOther} : InputProps) {
	// Brush bar handling
	const handleEyeL = (event: React.ChangeEvent<HTMLInputElement>) : number | undefined => {
		if (!canvasRef.current) { return; }
		const size:string = event.target.value;
		setEyeL(+size);
	}
	const handleEyeR = (event: React.ChangeEvent<HTMLInputElement>) : number | undefined => {
		if (!canvasRef.current) { return; }
		const size:string = event.target.value;
		setEyeR(+size);
	}
	const handleNose = (event: React.ChangeEvent<HTMLInputElement>) : number | undefined => {
		if (!canvasRef.current) { return; }
		const size:string = event.target.value;
		setNose(+size);
	}
	const handleMouth = (event: React.ChangeEvent<HTMLInputElement>) : number | undefined => {
		if (!canvasRef.current) { return; }
		const size:string = event.target.value;
		setMouth(+size);
	}
	const handleOther = (event: React.ChangeEvent<HTMLInputElement>) : number | undefined => {
		if (!canvasRef.current) { return; }
		const size:string = event.target.value;
		setOther(+size);
	}

	const saveImg = function(event: MouseEvent) {
		// get image URI from canvas object
		if (!canvasRef.current) { return; }
		var imageURI = canvasRef.current.toDataURL("image/jpg");
		if (!event) { return; }
		event.target.href = imageURI;
	};

	return (
		<div className='Buttons2'>
			<div className='Parameters'>
				<span> eyeL :
					<input type="range" id="paintRange" min="0.01" max="1.0" value={eyeL} step="0.01" onChange={handleEyeL}/>
				</span>
				<span> eyeR :
					<input type="range" id="paintRange" min="0.01" max="1.0" value={eyeR} step="0.01" onChange={handleEyeR}/>
				</span>
				<span> Nose :
					<input type="range" id="paintRange" min="0.01" max="1.0" value={nose} step="0.01" onChange={handleNose}/>
				</span>
				<span> Mouth :
					<input type="range" id="paintRange" min="0.01" max="1.0" value={mouth} step="0.01" onChange={handleMouth}/>
				</span>
				<span> Other :
					<input type="range" id="paintRange" min="0.01" max="1.0" value={other} step="0.01" onChange={handleOther}/>
				</span>
				<button className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded inline-flex items-center">
					<svg className="fill-current w-4 h-4 mr-2" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><path d="M13 8V2H7v6H2l8 8 8-8h-5zM0 18h20v2H0v-2z"/></svg>
					<a id="download" download="myImage.jpg" href="" onClick={saveImg}>Download to myImage.jpg</a>
				</button>
			</div>
		</div>
	);
}

export default Parameters;
