import '../styles/styles.css';

interface InputProps {
	canvasRef: React.RefObject<HTMLCanvasElement>;
	eyeL: number;
	eyeR: number;
	nose: number;
	mouth: number;
	other: number;
	drawingstate: Boolean;
	brushsize: number;
	erasersize: number;
	setBrushsize: React.Dispatch<React.SetStateAction<number>>;
	setErasersize: React.Dispatch<React.SetStateAction<number>>;
	setDrawingstate: React.Dispatch<React.SetStateAction<Boolean>>;
}

function Buttons({canvasRef, eyeL, eyeR, nose, mouth, other, drawingstate,
	brushsize, erasersize, setBrushsize, setErasersize, setDrawingstate} : InputProps) {

	// Convert Button
	// const convertCanvas = () => {
	// 	if (!canvasRef.current) {
	// 		return;
	// 	}
	// 	const dataURL:string = canvasRef.current.toDataURL("image/png");
	// }

	const handleBrush = (event: React.ChangeEvent<HTMLInputElement>) : number | undefined => {
		if (!canvasRef.current) { return; }
		const size:string = event.target.value;
		setBrushsize(+size);
	}
	const handleEraser = (event: React.ChangeEvent<HTMLInputElement>) : number | undefined => {
		if (!canvasRef.current) { return; }
		const size:string = event.target.value;
		setErasersize(+size);
	}

	const modeChange = () => {
		if (!canvasRef.current) { return; }
		setDrawingstate(!drawingstate);
	}

	const clearCanvas = () => {
		if (!canvasRef.current) { return; }
		const canvas: HTMLCanvasElement = canvasRef.current;
		canvas.getContext('2d')!!.clearRect(0, 0, canvas.width, canvas.height);
	}

	// Realtime Button

	const [undo, setUndo] = useState(0);
	const [redo, setRedo] = useState(0);
	const undoLastOperation = () => {
		if (undo > 0) {
		  const data = undoSteps[undo];
		  contextRef.current.strokeStyle = "white";
		  contextRef.current.beginPath();
		  contextRef.current.lineWidth = 5;
		  contextRef.current.moveTo(data[0].offsetX, data[0].offsetY);
		  data.forEach((item, index) => {
			if (index !== 0) {
			  contextRef.current.lineTo(item.offsetX, item.offsetY);
			  contextRef.current.stroke();
			}
		  });
		  contextRef.current.closePath();
		  contextRef.current.strokeStyle = "black";
		  const temp = {
			...undoSteps,
			[undo]: []
		  };
		  const te = {
			...redoStep,
			[redo + 1]: [...data]
		  };
		  setUndo(undo - 1);
		  setRedo(redo + 1);
		  setRedoStep(te);
		  setUndoSteps(temp);
		}
	  };

	  const redoLastOperation = () => {
		if (redo > 0) {
		  const data = redoStep[redo];
		  contextRef.current.strokeStyle = "black";
		  contextRef.current.beginPath();
		  contextRef.current.lineWidth = 5;
		  contextRef.current.moveTo(data[0].offsetX, data[0].offsetY);
		  data.forEach((item, index) => {
			if (index !== 0) {
			  contextRef.current.lineTo(item.offsetX, item.offsetY);
			  contextRef.current.stroke();
			}
		  });
		  contextRef.current.closePath();
		  const temp = {
			...redoStep,
			[redo]: []
		  };
		  setUndo(undo + 1);
		  setRedo(redo - 1);
		  setRedoStep(temp);
		  setUndoSteps({
			...undoSteps,
			[undo + 1]: [...data]
		  });
		}
	  };

	return (
		<div className='Buttons1'>
			<div className='Buttonblocks'>
				<span> Brush size : </span>
				<input type="range" id="paintRange" min="0.1" max="10" value={brushsize} step="0.1" onChange={handleBrush}/>
				<span> Paint size : </span>
				<input type="range" id="eraseRange" min="0.1" max="10" value={erasersize} step="0.1" onChange={handleEraser}/>
			</div>
			<div className='Buttonblocks'>
				<button className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded" onClick={clearCanvas}>
				Real Time
				</button>
				<button className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded" onClick={clearCanvas}>
				Convert
				</button>
				<button className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded" onClick={modeChange}>
					{drawingstate ? "BRUSH" : "ERASER"}
				</button>
			</div>
			<div className='Buttonblocks'>
				<button className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded" onClick={clearCanvas}>
				Clear
				</button>
				<button className="bg-blue-500 hover:bg-blue-400 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded" onClick={undohandling}>
				Undo
				</button>
				<button className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded inline-flex items-center">
					<svg className="fill-current w-4 h-4 mr-2" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><path d="M13 8V2H7v6H2l8 8 8-8h-5zM0 18h20v2H0v-2z"/></svg>
					<span>Download</span>
				</button>
			</div>
		</div>
	);
}

export default Buttons;
